import React, { Component, Fragment } from 'react';
import Select from 'react-select';
import Animated from 'react-select/animated';
import Resumen from './Resumen';
class ContenidoPedido extends Component {
    state = { 
        productos: [],
        total: 0
     }

    seleccionarProducto = (productos)=>{
        this.setState({
         productos   
        })
    }
    actualizarTotal =() =>{
        const productos = this.state.productos;
        if(productos.length === 0){
            this.setState({
                total: nuevoTotal
            });
            return;
        }
        let nuevoTotal = 0;
       
        productos.map(producto => nuevoTotal += (producto.cantidad * producto.precio));
        this.setState({
            total: nuevoTotal
        });
       
    }
    actualizarCantidad = (cantidad, index) => {
        const productos = this.state.productos;
        productos[index].cantidad = Number(cantidad);
        productos.map(producto => nuevoTotal += (producto.cantidad * producto.precio));
        //Actualizar la cantidad de los productos

        //Validamos

        //Agregar al state
        this.setState({
            productos
        }),() =>{
            this.actualizarTotal();
        }
    }

    eliminarProducto = (id) => {
        const productos = this.state.productos;
        const productoRestante = productos.filter(producto => producto.id !== id);
        
        this.setState({
            productos: productoRestante
        })
    }
    render() { 
        return ( 
            <Fragment>
            <h2 className="text-center mb-5">Seleccionar Articulos</h2>
            <Select options={this.props.productos}
            onChange={this.seleccionarProducto}
            isMulti={true}
            components={Animated()} 
            placeholder="Seleccionar Productos"
            getOptionValue={(options)=>options.id}
            getOptionLabel={(options)=>options.nombre}
            value={this.state.productos}/>
            <Resumen
              productos = {this.state.productos}  
              actualizarCantidad = {this.actualizarCantidad}
              eliminarProducto = {this.eliminarProducto}
            />
            <p className="font-weight-bold float-right mt-4">
                Total:
                <span className="font-wight-normal">
                    $ {this.state.total}
                </span>
            </p>
            </Fragment>
         );
    }
}
 
export default ContenidoPedido;
