import React, { Component, Fragment } from 'react';
import { Mutation } from 'react-apollo';
import {ACTUALIZAR_PRODUCTO} from '../../mutations';
const initialState = {
    nombre: '',
    precio: '',
    stock: ''
}
class FormularioEditar extends Component {
    state = { 
        ...this.props.producto.obtenerProducto
     }

     limpiarState = () =>{
        this.setState({
         ...initialState    
        })  
      }
     actualizarState = e => {
     const {name, value} = e.target; 
     
     this.setState({
         [name]: value
     })
     }
     
     validarForm =() =>{
         const{nombre, precio, stock} = this.state; 
 
         const noValido = !nombre || !precio || !stock;
 
         return noValido;
     }
    render() { 
        const {nombre, precio, stock} = this.state;
        const input = {
            nombre,
            precio: Number(precio),
            stock: Number(stock)
        }
        return ( 
            <Fragment>
            <h1>Editar Producto</h1>
            <Mutation mutation={ACTUALIZAR_PRODUCTO}
                      variables={{input}}
                      key={this.props.id}
            >
                {(actualizarProducto, {loading, error, data})=>{

                }}
            </Mutation>
            <form 
            className="col-md-8" >
                <div className="form-group">
                    <label>Nombre:</label>
                    <input 
                        onChange={this.actualizarState}
                        type="text"
                        name="nombre" 
                        className="form-control" 
                        placeholder="Nombre del Producto"
                        value={nombre}
                    />
                </div>
                <div className="form-group">
                    <label>Precio:</label>
                    <div className="input-group">
                        <div className="input-group-prepend">
                            <div className="input-group-text">$</div>
                        </div>
                        <input 
                            onChange={this.actualizarState}
                            type="number" 
                            name="precio" 
                            className="form-control" 
                            placeholder="Precio del Producto"
                            value={precio}
                        />
                    </div>
                </div>
                <div className="form-group">
                    <label>Stock:</label>
                    <input 
                        onChange={this.actualizarState}
                        type="number" 
                        name="stock" 
                        className="form-control" 
                        placeholder="stock del Producto" 
                        value={stock}
                    />
                </div>
                <button 
                    disabled={ this.validarForm() }
                    type="submit" 
                    className="btn btn-success float-right">
                            Guardar Cambios
                </button>
            </form>
            </Fragment>
         );
    }
}
 
export default FormularioEditar;
