import React, {Fragment, Component} from 'react';
import { Query, Mutation } from 'react-apollo';
import { CLIENTES_QUERY } from '../queries';
import { ELIMINAR_CLIENTE } from '../mutations';
import {Link} from 'react-router-dom';
import Paginador, {paginador} from './Paginador';
class Clientes extends Component{
        limite = 10;    
    state = {
            paginador: {
                offset: 0,
                actual: 1
            }
        }
        paginaAnterior = () =>{
            console.log('Anterior...');
        }

        paginaSiguiente = () => {
            console.log('Siguiente...');
        }

        render(){
            return(
                <Query query={CLIENTES_QUERY} pollInterval={1000}>
     {({loading, error, data, startPolling, stopPolling}) =>{
         if(loading) return "Cargando...";
         if(error) return `Error: ${error.message}`;
        console.log(data);
         return(
             <Fragment>
                 <h2 className="text-center">Listado Clientes</h2>
                 <ul className="list-group">
                    {data.getClientes.map(item =>{
                        const {id} = item;
                        return( 
                        <li key={item.id} className="list-group-item">
                            <div className="row justify-content-betwen align-items-center">
                                <div className="col-md-8 d-flex justify-content-betwen align-items-center">
                                    {item.nombre} {item.apellido}   
                                </div>
                                <div className="col-md-4 d-flex justify-content-end">
                                    <Mutation mutation={ELIMINAR_CLIENTE}>
                                        {eliminarCliente =>(
                                            <button type="button" 
                                            className="btn btn-danger d-block d-md-inline-block mr-2"
                                            onClick={()=>{
                                                if(window.confirm('Seguro que deseas eliminar el cliente?')){
                                        eliminarCliente({
                                            variables: {id}
                                                 })
                                            }
                                    }}>
                                        &times; Eliminar
                                    </button>
                                        )}
                                        
                                    </Mutation>
                                    <Link to={`/cliente/editar/${item.id}`} className="btn btn-success d-block d-md-inline-block">
                                        Editar Clientes
                                    </Link>

                                </div>
                            </div>
                        </li>
                        )    
                        })}    
                 </ul>
                 <Paginador 
                    actual = {this.state.paginador.actual}
                    totalClientes = {data.totalClientes}
                    limite = {this.limite}
                    paginaAnterior = {this.paginaAnterior}
                    paginaSiguiente = {this.paginaSiguiente}
                 />
             </Fragment>
             
         )
     }}       
    </Query>    
            )
        }
}
export default Clientes;
