import React from 'react';
import {Query, Mutation} from 'react-apollo';
import {OBTENER_PRODUCTO} from '../../queries';
import ResumenProducto from './ResumenProducto';
import {ACTUALIZAR_ESTADO} from '../../mutations';
import '../../Pedidos.css';
const Pedido = (props) => {
    const {pedido} = props;
    const fecha = new Date(Number(pedido.fecha));
    const {id, total} = pedido;
    const{estado} = pedido;
    let clase;
    if(estado==='PENDIENTE'){
        clase = 'border-light';
    }else if(estado==='CANCELADO'){
        clase = 'border-danger';
    }else {
        clase = 'border-success';
    }
    return ( 
        <div className="col-md-4">
            <div className={`card mb-3 ${clase}`}>
                <div className="card-body">
                    <p className="card-text font-weight-bold ">Estado:
                            <Mutation mutation={ACTUALIZAR_ESTADO}>
                                {actualizarEstado =>(
                                <select className="form-control my-3"
                                        value={pedido.estado}
                                        onChange={e =>{
                                            const input = {
                                                id,
                                                pedido: pedido.pedido,
                                                fecha: pedido.fecha,
                                                total, 
                                                cliente: props.cliente,
                                                estado: e.target.value
                                            }
                                            actualizarEstado({
                                                variables: {input}
                                            })
                                        }}>
                                        <option value="PENDIENTE">PENDIENTE</option>
                                        <option value="COMPLETADO">COMPLETADO</option>
                                        <option value="CANCELADO">CANCELADO</option>
                                </select>
                                )}
                            </Mutation>
                            
                    </p> 
                    <p className="card-text font-weight-bold">Pedido ID:&nbsp;
                        <span className="font-weight-normal"> {pedido.id}</span>
                    </p> 
                    <p className="card-text font-weight-bold">Fecha Pedido:&nbsp;  
                        <span className="font-weight-normal">
                            {fecha.toLocaleString("es-GT")}
                        </span>
                    </p>
                                       
                    <h3 className="card-text text-center mb-3">Art√≠culos del pedido</h3>
                    {pedido.pedido.map((producto, index) =>{
                        const {id} = producto;
                        return(
                            <Query key={pedido.id+index} query={OBTENER_PRODUCTO} 
                        variables={{id}}>
                     {({loading, error, data, refetch}) =>{
                         if(loading) return "Cargando...";
                         if(error) return `Error ${error.message}`;
                        return(  
                              <ResumenProducto   
                             producto={data.obtenerProducto} 
                             cantidad={producto.cantidad}
                             id={producto.id}     
                             />
                                 
                         )
                     }}       
                </Query>
                        );
                    })}
                    <p className="card-text font-weight-bold">Total:&nbsp;  
                         <p className="font-weight-normal"> 
                                ${pedido.total}
                        </p>
                    </p> 
                </div>
            </div>
        </div>
     );
}
 
export default Pedido;
